CREATE TABLE jobeet_addresses (id BIGINT AUTO_INCREMENT, title VARCHAR(255) NOT NULL, x FLOAT(18, 2), y FLOAT(18, 2), created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
CREATE TABLE jobeet_routes (id BIGINT AUTO_INCREMENT, price DECIMAL(18, 2) NOT NULL, address_id_a BIGINT, address_id_b BIGINT, created_at DATETIME NOT NULL, updated_at DATETIME NOT NULL, PRIMARY KEY(id)) ENGINE = INNODB;
